export default function Process() {
  const barRef = useRef<Sticky>(null);
  const { y } = useWindowScroll();
  const [tricker, setTricker] = useState<number[][]>([]);
  const [activeIndex, setActiveIndex] = useState(0);
  //
  const scrollItemGap = 400;
  useEffect(() => {
    setTimeout(() => {
      const arr: number[][] = [];

      Array.from({ length: 16 }).map((_, index) => {
        const el = document.getElementById(`p-item-${index}`);
        const top = el?.offsetTop || 0;
        const height = el?.clientHeight;

        arr.push([top, top + (height || 0)]);
      });
      setTricker(arr);
    }, 100);
  }, []);
  useEffect(() => {
    const index = tricker.findIndex((item) => {
      return y >= item[0] - scrollItemGap && y < item[1] - scrollItemGap;
    });
    console.log(index);
    if (index < 0) {
      return;
    }
    index !== activeIndex && setActiveIndex(index);
  }, [y]);
  return (
    <div className={styles.process}>
      <div className={styles["process-content"]}>
        <div className={styles["process-step"]}>
          <Sticky
            ref={barRef}
            enabled={true}
            top={0}
            bottomBoundary={"#p-item-15"}
          >
            <div className={styles["step-wrap"]}>
              <div style={{ height: 80 }}></div>
              <h2></h2>
              <Step activeIndex={activeIndex} />
            </div>
          </Sticky>
        </div>
        <div className={styles["process-items"]}>
          <div style={{ height: 80 }}></div>
          {Array.from({ length: 16 }).map((_, index) => (
            <div
              id={`p-item-${index}`}
              className={styles["process-item"]}
              key={String(index)}
            >
              <div className={styles["image-wrap"]}>
                <Image
                  src={`/images/cad/${index + 1}.png`}
                  alt=""
                  fill
                  className="image"
                />
              </div>
              <p>{textList[index]}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
